mapper.select:
select ps.seatId, ps.perfId, ps.timeId, ps.seatCol, ps.seatRow, ps.reserved
from performance_seat ps join performance_time pt on ps.timeId=pt.timeId
where pt.perfId = #{perfId} and pt.showDate = #{showDate}
and pt.showTime = #{showTime}
order by ps.seatRow asc, ps.seatCol asc

mapper.update:
update performance_seat set status='예약완료'
where perfId=#{perfId} and timeId=#{timeId} and seatId in


  #{id}
